import React, { useState } from 'react'
import './TopButton.css'

import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward'
import { Link } from 'react-scroll'

const TopButton = () => {

  const [topButtonVisible, setTopButtonVisible] = useState(false)
  // Cuando el usuario se desplace 20 píxeles hacia abajo desde la parte superior del documento, muestre el botón

  const scrollFunction = () => {
    if (
      document.body.scrollTop > window.innerHeight + 400 ||
      document.documentElement.scrollTop > window.innerHeight + 400
    ) {
      setTopButtonVisible(true)
    } else {
      setTopButtonVisible(false)
    }
  }

  window.onscroll = function () {
    scrollFunction()
  }
  window.onLoad = function () {
    scrollFunction()
  }

  return (
    <Link
      activeClass="active"
      to="about"
      spy={true}
      smooth={true}
      duration={500}
      offset={-63}
    >
      <button
        className={'topButton ' + (topButtonVisible ? 'on' : 'off')}
        title="Go to top"
      >
        <i aria-hidden="true">
          <ArrowUpwardIcon />
        </i>
      </button>
    </Link>
  )
}

export default TopButton
